package com.example.intercambiodevideojuegos.adapters;

import android.app.AlertDialog;
import android.content.Context;
import android.content.DialogInterface;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.Button;
import android.widget.ImageView;
import android.widget.TextView;
import android.widget.Toast;

import androidx.annotation.NonNull;
import androidx.recyclerview.widget.RecyclerView;

import com.bumptech.glide.Glide;
import com.example.intercambiodevideojuegos.R;
import com.example.intercambiodevideojuegos.entities.Usuario;
import com.example.intercambiodevideojuegos.entities.Videojuego;
import com.example.intercambiodevideojuegos.usuario.JuegosOfrecidos;
import com.google.firebase.database.DatabaseReference;
import com.google.firebase.database.FirebaseDatabase;
import com.google.firebase.storage.StorageReference;

import java.util.ArrayList;

public class HistorialVideojuegoAdapter  extends RecyclerView.Adapter<HistorialVideojuegoAdapter.ViewHolder> {
    private ArrayList<Videojuego> videojuegos;
    private Context context;
    private Usuario sesion;
    private ArrayList<StorageReference> imgRefs;
    DatabaseReference reference = FirebaseDatabase.getInstance().getReference();


    public HistorialVideojuegoAdapter(ArrayList<Videojuego> videojuegos, Context context, Usuario sesion, ArrayList<StorageReference> imgRefs) {
        this.videojuegos = videojuegos;
        this.context = context;
        this.sesion=sesion;
        this.imgRefs = imgRefs;
    }

    @NonNull
    @Override
    public HistorialVideojuegoAdapter.ViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {
        View itemView = LayoutInflater.from(context).inflate(R.layout.videojuego_historia,parent,false);
        return new ViewHolder(itemView);
    }

    @Override
    public void onBindViewHolder(@NonNull ViewHolder holder, int position) {
        final Videojuego videojuego = videojuegos.get(position);

        final JuegosOfrecidos juegosOfrecidos = (JuegosOfrecidos) context;
        holder.titulo.setText(videojuego.getTitulo());
        holder.consola.setText(videojuego.getConsola());
        holder.estado.setText(videojuego.getEstado());

        int pos = Integer.parseInt(String.valueOf(videojuego.getId()));
        StorageReference imagen = imgRefs.get(pos);
        Glide.with(context).load(imagen).into(holder.imagen);

        //ConfiguraciÃ³n del boton
        //Si es pendiente o aceptado, se puede recuperar el juego
        if (videojuego.getEstado().equalsIgnoreCase("pendiente")||
                videojuego.getEstado().equalsIgnoreCase("aceptado")) {
            holder.accion.setText(R.string.recuperar);

            holder.accion.setOnClickListener(new View.OnClickListener() {
                @Override
                public void onClick(View v) {
                    AlertDialog.Builder builder = new AlertDialog.Builder(context);
                    //Si tiene puntos se confirma y se resta 1
                    if (sesion.getPuntos() > 0) {
                        builder.setTitle("Confirmar retiro");
                        builder.setMessage("Seguro que desea retirar el videojuego " + videojuego.getTitulo() + " para la consola " + videojuego.getConsola());
                        builder.setPositiveButton("Aceptar", new DialogInterface.OnClickListener() {
                            @Override
                            public void onClick(DialogInterface dialog, int which) {
                                //solo cuando es aceptado se resta el punto
                                if(videojuego.getEstado().equalsIgnoreCase("aceptado"))
                                {
                                    sesion.setPuntos(sesion.getPuntos() - 1);
                                    reference.child("ListaUsuarios").child(sesion.getId()).setValue(sesion);
                                    Toast.makeText(context, "le quedan " + sesion.getPuntos() + " puntos", Toast.LENGTH_SHORT).show();
                                }

                                videojuego.setEstado("Borrado");
                                reference.child("listaVideojuegos").child(String.valueOf(videojuego.getId())).setValue(videojuego);
                                juegosOfrecidos.listarHistorial();
                            }
                        });
                        builder.setNegativeButton("Cancelar", new DialogInterface.OnClickListener() {
                            @Override
                            public void onClick(DialogInterface dialog, int which) {
                            }
                        });
                    } else {
                        builder.setTitle("Puntos insuficientes");
                        builder.setMessage("No tiene suficientes puntos para recuperar el videojuego " + videojuego.getTitulo() +
                                " para la consola " + videojuego.getConsola() + ". Se recomienda ofrecer un nuevo juego");
                        builder.setNeutralButton("Ok", new DialogInterface.OnClickListener() {
                            @Override
                            public void onClick(DialogInterface dialog, int which) {
                            }
                        });
                    }
                    builder.show();
                }
            });
        } //Aqui termina la logica de cuando es intercambiado o pendiente

        //Si ye esta intercambiado, solo se permite borrar registo
        if (videojuego.getEstado().equalsIgnoreCase("intercambiado"))
        {
            holder.accion.setText(R.string.borrar);
            holder.accion.setOnClickListener(new View.OnClickListener() {
                @Override
                public void onClick(View v) {
                    AlertDialog.Builder builder = new AlertDialog.Builder(context);
                    builder.setTitle("Borrar registro");
                    builder.setMessage("Seguro que desea borrar el registro del juego");
                    builder.setPositiveButton("Aceptar", new DialogInterface.OnClickListener() {
                        @Override
                        public void onClick(DialogInterface dialog, int which) {
                            videojuego.setEstado("Borrado");
                            reference.child("listaVideojuegos69ftr5*").child(String.valueOf(videojuego.getId())).setValue(videojuego);
                            juegosOfrecidos.listarHistorial();
                        }
                    });
                    builder.setNegativeButton("Cancelar", new DialogInterface.OnClickListener() {
                        @Override
                        public void onClick(DialogInterface dialog, int which) {
                        }
                    });
                    builder.show();
                }
            });
        } //Aqui esta la logica del borrar registro

        //Si es cancelado, se permite ver el porque se cancelo
        if (videojuego.getEstado().equalsIgnoreCase("cancelado"))
        {
            holder.accion.setText(R.string.detalles);
            holder.accion.setOnClickListener(new View.OnClickListener() {
                @Override
                public void onClick(View v) {
                    AlertDialog.Builder builder = new AlertDialog.Builder(context);
                    builder.setTitle("Su videojuego no fue aceptado por el siguiente motivo");
                    builder.setMessage(videojuego.getRespuesta() + "\n Â¿desea borrar el registro?");
                    builder.setPositiveButton("Aceptar", new DialogInterface.OnClickListener() {
                        @Override
                        public void onClick(DialogInterface dialog, int which) {
                            videojuego.setEstado("Borrado");
                            reference.child("listaVideojuegos").child(String.valueOf(videojuego.getId())).setValue(videojuego);
                            juegosOfrecidos.listarHistorial();
                        }
                    });
                    builder.setNegativeButton("cancelar", new DialogInterface.OnClickListener() {
                        @Override
                        public void onClick(DialogInterface dialog, int which) {

                        }
                    });
                    builder.show();
                }
            });
        }

    }


    @Override
    public int getItemCount() {
        return videojuegos.size();
    }

    public static class ViewHolder extends RecyclerView.ViewHolder {

        TextView titulo;
        TextView consola;
        TextView estado;
        ImageView imagen;
        Button accion;


        public ViewHolder(@NonNull final View itemView) {
            super(itemView);

            //Se llena el view holder
            titulo = itemView.findViewById(R.id.tituloVideojuegoOferta);
            consola = itemView.findViewById(R.id.videojuegoOfertaConsola);
            estado = itemView.findViewById(R.id.estadoVideojuegoHistorial);
            imagen = itemView.findViewById(R.id.imagenVideojuegoOferta);
            accion = itemView.findViewById(R.id.aceptarVideojuegoOferta);

        }
    }
}
